mapper.update:
update app_desc 
	set name=#{name}, user_id=#{userId}, status=#{status}, intro=#{intro}, create_time=#{createTime},
		passed_time=#{passedTime},type=#{type},
		officer=#{officer},ver_id=#{verId},mem_alert_value=#{memAlertValue},client_conn_alert_value=#{clientConnAlertValue},important_level=#{importantLevel}
where app_id=#{appId}

mapper.select:
	select count(app_id) from app_desc where 1=1
    
        
        	and name like '${appName}%'
   	 	
    
    
        
        	and type = ${appType}
   	 	
    
    
        
        	and status = ${appStatus}
   	 	
    
    
        
        	and app_id = ${appId}
   	 	
    
    
        
        	and important_level = ${importantLevel}

    	select           
        	
        from app_desc where 1=1
        
            
            	and name like '${appName}%'
       	 	
        
        
            
            	and type = ${appType}
       	 	
        
        
            
            	and status = ${appStatus}
       	 	
        
        
            
            	and app_id = ${appId}
       	 	
        
        
            
            	and important_level = ${importantLevel}
       	 	
        
        
        	
        		
		        	
            			limit #{page.start},#{page.pageSize};
		        	
		        	
		        	    limit #{page.totalCount}
