mapper.select:
select id,,task_id,create_time,operate_id,refuse_reason
from app_audit

    
        and status=#{status}
        and status in (0,2)
    
    
        and type=#{type}
    
    
        and id=#{auditId}
    
    
        and user_id=#{userId}
    
    
        and operate_id=#{operateId}
    

order by create_time desc

select id,,create_time,refuse_reason
from app_audit
where id=#{id}

select id,,create_time,refuse_reason
from app_audit
where app_id=#{appId}

select id,,create_time,refuse_reason
from app_audit
where app_id=#{appId} and type=#{type}

select sum(1) count,status
from app_audit

    
        and operate_id=#{operateId}
    
    
        and user_id=#{userId}
    
    
        and modify_time between #{startTime} and #{endTime}
        
    

group by status;

select sum(1) count,type
from app_audit

    
        and operate_id=#{operateId}
    
    
        and user_id=#{userId}
    
    
        and modify_time between #{startTime} and #{endTime}
        
    

group by type;
